import { fetchRequest } from './rest'

export function searchUsers (body) {
  return fetchRequest('/search/users',
    'get',
    body)
}

export function getRepos (user) {
  return fetchRequest(`/users/${user}/repos`, 'get')
}

export function getStarred (user) {
  return fetchRequest(`/users/${user}/starred`, 'get')
}

export function getFollowers (user) {
  return fetchRequest(`/users/${user}/followers`, 'get')
}

export function getFollowing (user) {
  return fetchRequest(`/users/${user}/following`, 'get')
}
