import React from 'react'
import PropTypes from 'prop-types'
import './style.css'

const Sidebar = ({ user }) => {
  if (user) {
    const { avatar_url, html_url, login, score } = user
    return (
      <div className="userProfile__sidebar">
        <img className="image" alt="user profile avatar" src={avatar_url} />
        <h2 className="name">{login}</h2>
        <p className="score">score: {score}</p>
        <a className="page-link" target="_blank" href={html_url}>github page</a>
      </div>
    )
  }
  return null
}

Sidebar.propTypes = {
  user: PropTypes.object,
}

export default Sidebar
