import { connect } from 'react-redux'
import Panel from 'components/User/Profile/Panel'
import { getActiveTab, getRepos, getFollowers, getFollowing, getStars, isLoading } from 'redux/profile/selectors'

const mapStateToProps = state => ({
  activeTab: getActiveTab(state),
  repos: getRepos(state),
  followers: getFollowers(state),
  following: getFollowing(state),
  stars: getStars(state),
  isLoadingRepos: isLoading(state, 'repos'),
  isLoadingFollowers: isLoading(state, 'followers'),
  isLoadingFollowing: isLoading(state, 'following'),
  isLoadingStars: isLoading(state, 'stars'),
})

const mergeProps = (stateProps, dispatchProps, ownProps) => {
  return {
    ...stateProps,
    ...dispatchProps,
    ...ownProps,
  }
}

export default connect(mapStateToProps, null, mergeProps)(Panel)
